import React, { useState, useEffect, useRef } from "react";
import Webcam from "react-webcam";
import "./camera.css"
import Header from '../header/header';
import Step2 from '../step2/step2';
  let toggle = "stop";

  const TestOverlay = () => {
    const [buttonName,setButtonName] = useState("start tracking");
    const webcamRef = React.useRef(null);
    const [imgSrc, setImgSrc] = React.useState(null);

    const [result, setResult] = useState("");
    const canvasRef = useRef();
    const imageRef = useRef();
    const videoRef = useRef();
    const [posture, setPosture] = useState(0);
    // Get camera feed
    useEffect(() => {
      async function getCameraStream() {
        const stream = await navigator.mediaDevices.getUserMedia({
          audio: false,
          video: true,
        });
    
        if (videoRef.current) {      
          videoRef.current.srcObject = stream;
        }
      };
    
      getCameraStream();
    }, []);

    // Send iage to API
    useEffect(() => {
      const interval = setInterval(async () => {
        if(toggle == "stop"){
          return () => clearInterval(interval);
        } else {
          captureImageFromCamera();

          if (imageRef.current) {
            const formData = new FormData();
            formData.append('image', imageRef.current);

            const response = await fetch('/classify', {
              method: "POST",
              body: formData,
            });

            // setResult(response.status)

            if (response.status === 200) {
              const text = await response.text();
              setResult(text);
              if(text!="0") setPosture(posture+1);
              else setPosture(0);
              if(posture == 3) goAlert();
            } else {
              setResult("Error from API. ");
            }
          }
        }
      }, 5000); // <- interval in ms
      return () => clearInterval(interval);
    }, []);
    const sound1 = new Audio('/src/sound1.mp3');
    const sound2 = new Audio('/src/sound2.mp3');
    const sound3 = new Audio('/src/sound3.mp3');
    const sound4 = new Audio('/src/sound4.mp3');
    const goAlert = () =>{
      if(Step2.alert==0) alertSound();
      else if(Step2.alert == 1) alertPop();
      else
      {
        alertSound();
        alertPop();
      }
    }
    function alertSound(){
      if(Header.sound == "sound1") {
        console.log("sound1");
        sound1.play();
        sound1.loop = false;
        sound1.pause();
      }
      if(Header.sound == "sound2") {
        console.log("sound2");
        sound2.play();
        sound2.loop = false;
        sound2.pause();
      }
      if(Header.sound == "sound3") {
        console.log("sound3");
        sound3.play();
        sound3.loop = false;
        sound3.pause();
      }
      if(Header.sound == "sound4") {
        console.log("sound4");
        sound4.play();
        sound4.loop = false;
        sound4.pause();
      }
    }
    function alertPop(){
      alert();
    }
    const playCameraStream = () => {
      if (videoRef.current) {
        videoRef.current.play();
      }
    };

    const captureImageFromCamera = () => {
      const context = canvasRef.current.getContext('2d');
      const { videoWidth, videoHeight } = videoRef.current;
  
      canvasRef.current.width = videoWidth;
      canvasRef.current.height = videoHeight;
  
      context.drawImage(videoRef.current, 0, 0, videoWidth, videoHeight);
  
      canvasRef.current.toBlob((blob) => {
        imageRef.current = blob;
      })
    };

    const toggleAlert=function(){
      if(toggle=="stop") {
        toggle = "start";
        setButtonName("stop tracking");
      }
      else if(toggle=="start") {
        toggle = "stop";
        setButtonName("start tracking");
      }
    }
  
    return (
      <>
        <main>
          <video ref={videoRef} onCanPlay={() => playCameraStream()} id="video" />
          <canvas ref={canvasRef} hidden></canvas>
          <p>Currently seeing: {result}</p>
        </main>
        <div className ="startTrack">
          <button className="startBut" onClick={toggleAlert}>
              {buttonName}
          </button>
          
        </div>
        {imgSrc && (
          <img
            src={imgSrc}
          />
        )}
      </>
    );
  };

  export default TestOverlay
