import React, { useState } from "react";
import "./header.css"
import {Link,BrowserRouter as Router} from 'react-router-dom';
import Modal from 'react-modal';

const customStyles = {
    content: {
      right: 'auto',
      bottom: 'auto',
      marginLeft: '90%',
      marginTop: '-80px',
      transform: 'translate(-50%, -50%)',
    },
  };
  Modal.setAppElement('#root');

const Header = () => {
    let sub;
    const [modalIsOpen, setIsOpen] = useState(false);

    function openModal() {
        setIsOpen(true);
      }
    
      function afterOpenModal() {
        sub.style.color = '#033A2B';
      }
    
      function closeModal() {
        setIsOpen(false);
      }
    return (
        <div className="header">
            <div id = "title">
                <Link to = "/">
                ETN
                </Link>
            </div>
            <div id = "subtitle">
                escape from tech neck
            </div>
            <>
            <button className = "settingButton" onClick ={openModal}>
            </button>
            <Modal isOpen={modalIsOpen}  onRequestClose={closeModal}>
                <div className="settings">Settings</div>
                <div className ="sounds">
                    Sounds
                    <ul>
                        <li>Alerting Sound</li>
                        <li>Volume</li>
                    </ul>
                </div>
                <div className ="break">
                    Break time
                    <ul>
                        <li>alarm every</li>
                    </ul>
                </div>
            </Modal>
            </>
            <div className = "menu">
            <div class = "option">
            <Link to ="/start/step1">
                Start
            </Link>
            </div>
            <div class = "option">
            <Link to ="/exercise">
                Exercise 
            </Link>
            </div>
            <div class = "option">
            <Link to ="/help">
                Help 
            </Link>
            </div>
            <div class = "option">
            <Link to ="/record">
                Record
            </Link>
            </div>
            </div>
            <hr/>
        </div>
    );
    
  };
export default Header
