import React from 'react'
import { Line,Bar } from 'react-chartjs-2'
import { Chart, registerables } from 'chart.js';
import "./record.css"
Chart.register(...registerables);
const legend = {
    display: false,
  };
const options = {
    spanGaps: true,
    maxBarThickness: 30,
    grouped: true,
    interaction: {
      mode: 'index',
    },
  };

let [pro0,pro1,pro2,pro3,pro4] =0;
const Record = (props) => {
    var posture0 = props.posture0;
    var posture1 = props.posture1; 
    var posture2 = props.posture2;
    var posture3 = props.posture3;
    var posture4 = props.posture4;
    const calpro = function(){
      var tot = posture0 + posture1 + posture2 + posture3 + posture4;
      pro0 = posture0/tot;
      pro1 = posture1/tot;
      pro2 = posture2/tot;
      pro3 = posture3/tot;
      pro4 = posture4/tot;

    }
    const data = {
      datasets: [
          {
            type: 'bar',
            label: 'Your Posture Record',
            backgroundColor: '#09AA7C',
            data: [
              { x: 'good', y: {pro0} },
              { x: 'tech-neck', y: {pro1} },
              { x: 'leaned', y: {pro2} },
              { x: 'sleeping', y: {pro3} },
              { x: 'resting in chin', y: {pro4} }
            ],
            borderWidth: 0,
          },
          
        ],
    };
  return (
    <div className='chart'>
        Study Time:
        <br/>
        Good Posture:
        <br/>
        Recommended Exercise : 
        <br/>
      <Line type="line" data={data} options={options} legend = {legend}/>
    </div>
  );
};

export default Record;
