import React from 'react'
import { Line,Bar } from 'react-chartjs-2'
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const options = {
    spanGaps: true,
    maxBarThickness: 30,
    grouped: true,
    interaction: {
      mode: 'index',
    },
    
    scales: {
      x: {
        afterTickToLabelConversion: function (scaleInstance) {
          const ticks = scaleInstance.ticks;
  
          const newTicks = ticks.map((tick) => {
            return {
              ...tick,
              label: tick.label + 'ðŸŽµ'
            };
          });
  
          scaleInstance.ticks = newTicks;
        },
        grid: {
          display: false,
          drawTicks: true,
          tickLength: 4,
          color: '#E2E2E230'
        },
        axis: 'x',
        position: 'bottom',
        ticks: {
          minRotation: 45,
          padding: 5,
        },
      },
      y: {
        type: 'linear',
        grid: {
          color: '#E2E2E230',
        },
        afterDataLimits: (scale) => {
          scale.max = scale.max * 1.2;
        },
        axis: 'y',
        display: true,
        position: 'left',
        title: {
          display: true,
          align: 'end',
          color: '#808080',
          font: {
            size: 12,
            family: "'Noto Sans KR', sans-serif",
            weight: 300,
          },
          text: 'ë‹¨ìœ„: ë°°'
        }
      },
      y_sub: {
        position: 'right',
        title: {
          display: true,
          align: 'end',
          color: '#808080',
          font: {
            size: 12,
            family: "'Noto Sans KR', sans-serif",
            weight: 300,
          },
          text: 'ë‹¨ìœ„: ë°°'
        },
        afterDataLimits: (scale) => {
          scale.max = scale.max * 1.2;
        },
      },
    }
  };
  const data = {
    datasets: [
        {
          type: 'line',
          label: 'Dataset 1',
          borderColor: 'rgb(54, 162, 235)',
          borderWidth: 2,
          data: [
            { x: 'good', y: 1 },
            { x: 'tech-neck', y: 2 },
            { x: 'leaned', y: 3 },
            { x: 'sleeping', y: null },
            { x: 'resting in chin', y: 5 }
          ],
          yAxisID: 'y_sub',
        },
        {
          type: 'bar',
          label: 'Dataset 2',
          backgroundColor: 'rgb(255, 99, 132)',
          data: [
            { x: 'good', y: 1 },
            { x: 'tech-neck', y: 2 },
            { x: 'leaned', y: 3 },
            { x: 'sleeping', y: null },
            { x: 'resting in chin', y: 5 }
          ],
          borderColor: 'red',
          borderWidth: 2,
        },
        
      ],
  };
const Record = () => {
  return (
    <div>
      <Line type="line" data={data} options={options} />
    </div>
  );
};

export default Record;
