import React from 'react'
import { Line,Bar } from 'react-chartjs-2'
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const options = {
    legend: {
      display: true,
    },
    scales: {
      yAxes: [{
        ticks: { 
          min: 0, 
          stepSize: 10, 
        }
      }]
    },
    maintainAspectRatio: false 
  }
  const data = {
    // 각 막대별 라벨
    labels: ['good', 'tech-neck', 'leaned','sleeping', 'resting in chin'],
    datasets: [
      {
        borderWidth: 1, 
        data: [60,20,10,3,2],
        backgroundColor: '#09AA7C',
      }
    ]
  };
const Record = () => {
  return (
    <div>
      <Bar
  data={data}
  options={options}
  height={300}
/>
    </div>
  );
};

export default Record;
